mapper.select:
SELECT csb.id, ct.grade, ct.class_number, tt.teacher_name, csb.teacher_position, tt.`subject` FROM class_student_bind csb LEFT JOIN teacher_table tt ON tt.id = csb.teacher_table_id LEFT JOIN class_table ct ON ct.id = csb.class_table_id
AND tt.subject = #{subject}
AND tt.teacher_name LIKE CONCAT('%',#{teacherName},'%')
AND ct.grade = #{grade}
AND ct.class_number = #{classNumber}
ORDER BY csb.id DESC LIMIT #{index},#{size}

SELECT COUNT(*) FROM class_student_bind csb LEFT JOIN teacher_table tt ON tt.id = csb.teacher_table_id LEFT JOIN class_table ct ON ct.id = csb.class_table_id
AND tt.teacher_name LIKE CONCAT('%',#{teacherName},'%')
AND ct.grade = #{grade}
AND ct.class_number = #{classNumber}
